$(document).ready(
  (jQuery,
  void $(".client-single").on("click", function (a) {
    a.preventDefault();
    var t = $(this).hasClass("active"),
      s = $(this).parents(".testi-wrap");
    t ||
      ((a = s.find(".client-single.active")),
      (t = $(this).attr("data-position")),
      (s = a.attr("data-position")),
      a.removeClass("active").removeClass(s).addClass("inactive").addClass(t),
      a.attr("data-position", t),
      $(this)
        .addClass("active")
        .removeClass("inactive")
        .removeClass(t)
        .addClass(s),
      $(this).attr("data-position", s));
  }))
);
