const detailsTabs = document.querySelectorAll(".details-tab");
detailsTabs.forEach((n) => {
  n.addEventListener("click", () => {
    var t = n.parentElement;
    const e = document.querySelectorAll(".details-item");
    e.forEach((e) => {
      e !== t && e.removeAttribute("open");
    });
  }),
    n.addEventListener("mouseover", () => {
      var t = n.parentElement;
      const e = document.querySelectorAll(".details-item");
      e.forEach((e) => {
        e !== t && e.removeAttribute("open");
      });
    });
}),
  window.addEventListener("DOMContentLoaded", () => {
    const e = document.querySelectorAll(".details-item");
    e.forEach((e) => {
      e.removeAttribute("open");
    });
  }),
  document.addEventListener("DOMContentLoaded", function () {
    const e = document.getElementById("hamburger-icon"),
      t = document.getElementById("myNav");
    e.addEventListener("click", function () {
      t.classList.toggle("active");
    });
  });
