document.addEventListener('DOMContentLoaded', function () {
  const e = document.querySelectorAll('.details-tab'),
    o = document.querySelectorAll('.details-item')
  o.forEach((e) => {
    e.removeAttribute('open')
  }),
    e.forEach((n) => {
      n.addEventListener('mouseover', () => {
        var t = n.parentElement
        o.forEach((e) => {
          e !== t && e.hasAttribute('open') && e.removeAttribute('open')
        }),
          t.setAttribute('open', !0)
      }),
        n.addEventListener('click', () => {
          var e = n.parentElement,
            t = e.hasAttribute('open')
          o.forEach((e) => {
            e.removeAttribute('open')
          }),
            t || e.setAttribute('open', !0)
        })
    })
  const t = document.getElementById('hamburger-icon'),
    n = document.getElementById('myNav')
  t.addEventListener('click', function () {
    n.classList.toggle('active')
  })
})
