document.addEventListener("DOMContentLoaded", function () {
  const detailsTabs = document.querySelectorAll(".details-tab");
  const detailsItems = document.querySelectorAll(".details-item");

  // Fermer toutes les sections lors du chargement de la page
  detailsItems.forEach((item) => {
    item.removeAttribute("open");
  });

  detailsTabs.forEach((tab) => {
    // Ouvrir la section sur le survol
    tab.addEventListener("mouseover", () => {
      var parentItem = tab.parentElement;
      detailsItems.forEach((item) => {
        if (item !== parentItem && item.hasAttribute("open")) {
          item.removeAttribute("open");
        }
      });
      parentItem.setAttribute("open", true);
    });

    // Ouvrir/Fermer la section sur le clic
    tab.addEventListener("click", () => {
      var parentItem = tab.parentElement;
      const isOpen = parentItem.hasAttribute("open");
      detailsItems.forEach((item) => {
        item.removeAttribute("open");
      });
      if (!isOpen) {
        parentItem.setAttribute("open", true);
      }
    });
  });

  const hamburgerIcon = document.getElementById("hamburger-icon");
  const myNav = document.getElementById("myNav");
  hamburgerIcon.addEventListener("click", function () {
    myNav.classList.toggle("active");
  });
});
